import React, { useState, useEffect, useCallback } from 'react';
import { Calendar, Clock, Users, CheckCircle2, XCircle, LogOut, UserCheck, Shield, Lock, RefreshCw, Monitor } from 'lucide-react';

const AttendanceSystem = () => {
  // ç³»ç»ŸçŠ¶æ€
  const [currentTime, setCurrentTime] = useState(new Date());
  const [currentUser, setCurrentUser] = useState(null);
  const [currentView, setCurrentView] = useState('login');

  // IPç›¸å…³çŠ¶æ€
  const [userIP, setUserIP] = useState('');
  const [ipStatus, setIpStatus] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  // æ¶ˆæ¯æç¤º
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('info');

  // ç™»å½•è¡¨å•
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });

  // ç®¡ç†å‘˜ç»Ÿè®¡æ•°æ®
  const [adminStats, setAdminStats] = useState({});
  
  // ç®¡ç†å‘˜æ•°æ®
  const [employees, setEmployees] = useState([]);
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [users, setUsers] = useState([]);
  const [adminView, setAdminView] = useState('dashboard'); // dashboard, records, users

  // ç”¨æˆ·ç®¡ç†ç›¸å…³çŠ¶æ€
  const [showUserModal, setShowUserModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [userForm, setUserForm] = useState({
    username: '',
    password: '',
    role: 'employee',
    name: '',
    department: '',
    allowed_ips: []
  });

  // ç­¾åˆ°çŠ¶æ€
  const [checkinStatus, setCheckinStatus] = useState({ checked_in: false, checkin_time: null });

  // API åŸºç¡€ URL - ä¿®æ”¹ä¸ºæœ¬åœ°åœ°å€
  const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  // èŽ·å–å­˜å‚¨çš„ token
  const getToken = () => localStorage.getItem('attendance_token');

  // è®¾ç½® token
  const setToken = (token) => {
    if (token) {
      localStorage.setItem('attendance_token', token);
    } else {
      localStorage.removeItem('attendance_token');
    }
  };

  // API è¯·æ±‚å°è£…
  const apiRequest = async (endpoint, options = {}) => {
    const token = getToken();
    const config = {
      headers: {
        'Content-Type': 'application/json',
        ...(token && { Authorization: `Bearer ${token}` }),
        ...options.headers,
      },
      ...options,
    };

    try {
      console.log(`APIè¯·æ±‚: ${API_BASE}${endpoint}`, config);
      const response = await fetch(`${API_BASE}${endpoint}`, config);
      const data = await response.json();
      
      console.log(`APIå“åº”: ${endpoint}`, data);

      if (!response.ok) {
        throw new Error(data.error || `HTTP ${response.status}`);
      }

      return data;
    } catch (error) {
      console.error(`APIè¯·æ±‚é”™è¯¯ ${endpoint}:`, error);
      throw error;
    }
  };

  // èŽ·å–æœåŠ¡å™¨æ£€æµ‹çš„IPåœ°å€
  const fetchUserIP = useCallback(async () => {
    setIsLoading(true);
    setIpStatus('æ­£åœ¨ä»ŽæœåŠ¡å™¨èŽ·å–æœ¬æœºIPåœ°å€...');

    try {
      const data = await apiRequest('/ip');
      setUserIP(data.ip);
      setIpStatus(`æœåŠ¡å™¨æ£€æµ‹åˆ°IP: ${data.ip}`);
      console.log('[IPæ£€æµ‹] æœåŠ¡å™¨èŽ·å–æˆåŠŸ:', data.ip);
    } catch (error) {
      console.error('[IPæ£€æµ‹] æœåŠ¡å™¨èŽ·å–å¤±è´¥:', error);
      setUserIP('');
      setIpStatus(`èŽ·å–å¤±è´¥ï¼š${error.message}`);
    } finally {
      setIsLoading(false);
    }
  }, []);

  // éªŒè¯ token å¹¶è‡ªåŠ¨ç™»å½•
  const validateToken = useCallback(async () => {
    const token = getToken();
    if (!token) return;

    try {
      // é€šè¿‡èŽ·å–ç­¾åˆ°çŠ¶æ€æ¥éªŒè¯ token
      await apiRequest('/checkin/status');
      // å¦‚æžœæˆåŠŸï¼ŒèŽ·å–ç”¨æˆ·ä¿¡æ¯ï¼ˆä»Ž localStorage è§£æžï¼‰
      const userData = JSON.parse(localStorage.getItem('attendance_user'));
      if (userData) {
        setCurrentUser(userData);
        setCurrentView(userData.role === 'admin' ? 'admin' : 'checkin');
      }
    } catch (error) {
      console.log('Token æ— æ•ˆï¼Œéœ€è¦é‡æ–°ç™»å½•');
      setToken(null);
      localStorage.removeItem('attendance_user');
    }
  }, []);

  // èŽ·å–ç­¾åˆ°çŠ¶æ€
  const fetchCheckinStatus = useCallback(async () => {
    if (!currentUser || currentUser.role === 'admin') return;

    try {
      const data = await apiRequest('/checkin/status');
      setCheckinStatus(data);
    } catch (error) {
      console.error('èŽ·å–ç­¾åˆ°çŠ¶æ€å¤±è´¥:', error);
    }
  }, [currentUser]);

  // èŽ·å–ç®¡ç†å‘˜ç»Ÿè®¡æ•°æ®
  const fetchAdminStats = useCallback(async () => {
    if (!currentUser || currentUser.role !== 'admin') return;

    try {
      const data = await apiRequest('/admin/stats');
      setAdminStats(data);
    } catch (error) {
      console.error('èŽ·å–ç»Ÿè®¡æ•°æ®å¤±è´¥:', error);
      // å¦‚æžœæ²¡æœ‰è¿™ä¸ªAPIï¼Œæ˜¾ç¤ºé»˜è®¤ç»Ÿè®¡
      setAdminStats({
        total_employees: users.filter(u => u.role === 'employee').length,
        checked_in_today: attendanceRecords.filter(r => r.check_date === new Date().toISOString().split('T')[0]).length,
        attendance_rate: 0
      });
    }
  }, [currentUser, users, attendanceRecords]);

  // èŽ·å–è€ƒå‹¤è®°å½• - ä½¿ç”¨debugæŽ¥å£ä½œä¸ºä¸´æ—¶æ–¹æ¡ˆ
  const fetchAttendanceRecords = useCallback(async () => {
    if (!currentUser || currentUser.role !== 'admin') return;

    try {
      // å…ˆå°è¯•ç®¡ç†å‘˜API
      const data = await apiRequest('/admin/records');
      setAttendanceRecords(data.records || []);
    } catch (error) {
      console.error('èŽ·å–è€ƒå‹¤è®°å½•å¤±è´¥:', error);
      // å¦‚æžœæ²¡æœ‰è¿™ä¸ªAPIï¼Œè®¾ç½®ç©ºæ•°ç»„
      setAttendanceRecords([]);
    }
  }, [currentUser]);

  // èŽ·å–ç”¨æˆ·åˆ—è¡¨ - ä½¿ç”¨debugæŽ¥å£
  const fetchUsers = useCallback(async () => {
    if (!currentUser || currentUser.role !== 'admin') return;

    try {
      // å°è¯•ç®¡ç†å‘˜ç”¨æˆ·API
      try {
        const data = await apiRequest('/admin/users');
        setUsers(data.users || []);
        return;
      } catch (error) {
        console.log('ç®¡ç†å‘˜ç”¨æˆ·APIä¸å­˜åœ¨ï¼Œå°è¯•debugæŽ¥å£');
      }

      // å¤‡ç”¨ï¼šä½¿ç”¨debugæŽ¥å£
      const data = await apiRequest('/debug/users');
      const usersData = data.users || [];
      // è½¬æ¢æ•°æ®æ ¼å¼ï¼Œæ·»åŠ ç¼ºå¤±å­—æ®µ
      const formattedUsers = usersData.map(user => ({
        ...user,
        allowed_ips: user.allowed_ips ? JSON.parse(user.allowed_ips) : []
      }));
      setUsers(formattedUsers);
    } catch (error) {
      console.error('èŽ·å–ç”¨æˆ·åˆ—è¡¨å¤±è´¥:', error);
      setUsers([]);
    }
  }, [currentUser]);

  // åˆ›å»ºæˆ–æ›´æ–°ç”¨æˆ·
  const handleUserSubmit = async (e) => {
    e.preventDefault();

    try {
      setIsLoading(true);
      
      // å‡†å¤‡æäº¤æ•°æ®
      const submitData = {
        ...userForm,
        allowed_ips: JSON.stringify(userForm.allowed_ips)
      };

      if (editingUser) {
        // æ›´æ–°ç”¨æˆ· - å¦‚æžœæ²¡æœ‰ç®¡ç†å‘˜APIï¼Œæ˜¾ç¤ºé”™è¯¯
        showMessage('ç”¨æˆ·æ›´æ–°åŠŸèƒ½éœ€è¦å®Œæ•´çš„ç®¡ç†å‘˜APIæ”¯æŒ', 'error');
        return;
      } else {
        // åˆ›å»ºæ–°ç”¨æˆ· - å¦‚æžœæ²¡æœ‰ç®¡ç†å‘˜APIï¼Œæ˜¾ç¤ºé”™è¯¯
        showMessage('ç”¨æˆ·åˆ›å»ºåŠŸèƒ½éœ€è¦å®Œæ•´çš„ç®¡ç†å‘˜APIæ”¯æŒ', 'error');
        return;
      }

    } catch (error) {
      showMessage(error.message, 'error');
    } finally {
      setIsLoading(false);
    }
  };

  // åˆ é™¤ç”¨æˆ·
  const deleteUser = async (userId, userName) => {
    showMessage('ç”¨æˆ·åˆ é™¤åŠŸèƒ½éœ€è¦å®Œæ•´çš„ç®¡ç†å‘˜APIæ”¯æŒ', 'error');
  };

  // ç¼–è¾‘ç”¨æˆ·
  const editUser = (user) => {
    setEditingUser(user);
    setUserForm({
      username: user.username,
      password: '', // å¯†ç å­—æ®µç•™ç©º
      role: user.role,
      name: user.name,
      department: user.department,
      allowed_ips: Array.isArray(user.allowed_ips) ? user.allowed_ips : []
    });
    setShowUserModal(true);
  };

  // æ–°å¢žç”¨æˆ·
  const addNewUser = () => {
    setEditingUser(null);
    setUserForm({
      username: '',
      password: '',
      role: 'employee',
      name: '',
      department: '',
      allowed_ips: [userIP] // é»˜è®¤æ·»åŠ å½“å‰IP
    });
    setShowUserModal(true);
  };

  useEffect(() => {
    fetchUserIP();
    validateToken();

    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => clearInterval(timer);
  }, [fetchUserIP, validateToken]);

  useEffect(() => {
    if (currentUser && currentUser.role === 'admin') {
      fetchUsers();
      fetchAdminStats();
      fetchAttendanceRecords();
    } else if (currentUser) {
      fetchCheckinStatus();
    }
  }, [currentUser, fetchUsers, fetchAdminStats, fetchAttendanceRecords, fetchCheckinStatus]);

  // CSSæ ·å¼
  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #eff6ff 0%, #ddd6fe 100%)',
      padding: '1rem'
    },
    card: {
      backgroundColor: 'white',
      borderRadius: '0.5rem',
      boxShadow: '0 10px 25px rgba(0, 0, 0, 0.1)',
      padding: '2rem',
      marginBottom: '1.5rem'
    },
    button: {
      backgroundColor: '#2563eb',
      color: 'white',
      padding: '0.75rem 1rem',
      borderRadius: '0.375rem',
      border: 'none',
      cursor: 'pointer',
      fontWeight: '500',
      fontSize: '0.875rem',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem'
    },
    buttonSmall: {
      backgroundColor: '#6b7280',
      color: 'white',
      padding: '0.25rem 0.5rem',
      borderRadius: '0.25rem',
      border: 'none',
      cursor: 'pointer',
      fontSize: '0.75rem',
      display: 'flex',
      alignItems: 'center',
      gap: '0.25rem'
    },
    buttonText: {
      backgroundColor: 'transparent',
      color: '#dc2626',
      border: 'none',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      padding: '0.5rem 1rem'
    },
    input: {
      width: '100%',
      padding: '0.75rem',
      border: '1px solid #d1d5db',
      borderRadius: '0.375rem',
      fontSize: '1rem',
      outline: 'none',
      boxSizing: 'border-box'
    },
    badge: {
      padding: '0.25rem 0.5rem',
      borderRadius: '9999px',
      fontSize: '0.75rem',
      fontWeight: '500',
      display: 'inline-block'
    },
    successBadge: {
      backgroundColor: '#dcfce7',
      color: '#166534'
    },
    errorBadge: {
      backgroundColor: '#fee2e2',
      color: '#991b1b'
    },
    flexBetween: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    flexCenter: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center'
    },
    messageSuccess: {
      backgroundColor: '#dcfce7',
      color: '#166534',
      borderColor: '#bbf7d0'
    },
    messageError: {
      backgroundColor: '#fee2e2',
      color: '#991b1b',
      borderColor: '#fecaca'
    },
    messageWarning: {
      backgroundColor: '#fef3c7',
      color: '#92400e',
      borderColor: '#fde68a'
    }
  };

  // æ¶ˆæ¯æç¤º
  const showMessage = (text, type = 'info') => {
    setMessage(text);
    setMessageType(type);
    setTimeout(() => setMessage(''), 5000);
  };

  // å¤„ç†ç™»å½•
  const handleLogin = async () => {
    try {
      setIsLoading(true);
      console.log('å°è¯•ç™»å½•:', loginForm.username, API_BASE);
      
      const data = await apiRequest('/login', {
        method: 'POST',
        body: JSON.stringify(loginForm),
      });

      setToken(data.token);
      localStorage.setItem('attendance_user', JSON.stringify(data.user));
      setCurrentUser(data.user);
      setCurrentView(data.user.role === 'admin' ? 'admin' : 'checkin');
      showMessage('ç™»å½•æˆåŠŸï¼', 'success');
    } catch (error) {
      showMessage(error.message, 'error');
    } finally {
      setIsLoading(false);
    }
  };

  // å¤„ç†ç™»å‡º
  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('attendance_user');
    setCurrentUser(null);
    setCurrentView('login');
    setLoginForm({ username: '', password: '' });
    showMessage('å·²å®‰å…¨é€€å‡º', 'success');
  };

  // å¤„ç†ç­¾åˆ°
  const handleCheckIn = async () => {
    try {
      setIsLoading(true);
      const data = await apiRequest('/checkin', {
        method: 'POST',
      });

      setCheckinStatus({
        checked_in: true,
        checkin_time: data.checkin_time
      });

      showMessage(`ç­¾åˆ°æˆåŠŸï¼æ—¶é—´ï¼š${data.checkin_time}`, 'success');
    } catch (error) {
      if (error.message.includes('ä»Šæ—¥å·²ç­¾åˆ°')) {
        showMessage('ä»Šæ—¥å·²ç­¾åˆ°ï¼Œæ— éœ€é‡å¤ç­¾åˆ°', 'warning');
      } else {
        showMessage(error.message, 'error');
      }
    } finally {
      setIsLoading(false);
    }
  };

  // æµ‹è¯•è¿žæŽ¥åŠŸèƒ½
  const testConnection = async () => {
    try {
      setIsLoading(true);
      showMessage('æ­£åœ¨æµ‹è¯•è¿žæŽ¥...', 'info');
      
      // æµ‹è¯•åŸºæœ¬è¿žæŽ¥
      const ipData = await apiRequest('/ip');
      console.log('IPæµ‹è¯•æˆåŠŸ:', ipData);
      
      // æµ‹è¯•ç”¨æˆ·æ•°æ®
      const userData = await apiRequest('/debug/users');
      console.log('ç”¨æˆ·æ•°æ®æµ‹è¯•æˆåŠŸ:', userData);
      
      showMessage(`è¿žæŽ¥æµ‹è¯•æˆåŠŸï¼å‘çŽ° ${userData.users.length} ä¸ªç”¨æˆ·`, 'success');
    } catch (error) {
      showMessage(`è¿žæŽ¥æµ‹è¯•å¤±è´¥: ${error.message}`, 'error');
    } finally {
      setIsLoading(false);
    }
  };

  // ç™»å½•é¡µé¢
  if (currentView === 'login') {
    return (
      <div style={styles.container}>
        <div style={{...styles.flexCenter, minHeight: '100vh'}}>
          <div style={{...styles.card, width: '100%', maxWidth: '400px'}}>
            <div style={{textAlign: 'center', marginBottom: '2rem'}}>
              <div style={{
                backgroundColor: '#2563eb',
                padding: '0.75rem',
                borderRadius: '50%',
                width: '4rem',
                height: '4rem',
                margin: '0 auto 1rem',
                ...styles.flexCenter
              }}>
                <Lock size={32} color="white" />
              </div>
              <h1 style={{fontSize: '1.5rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '0.5rem'}}>
                è€ƒå‹¤ç³»ç»Ÿ
              </h1>
              <p style={{color: '#6b7280', marginTop: '0.5rem'}}>è¯·ä½¿ç”¨æŽˆæƒè´¦å·ç™»å½•</p>
              <p style={{color: '#059669', fontSize: '0.875rem', marginTop: '0.5rem'}}>
                APIåœ°å€: {API_BASE}
              </p>
            </div>

            {/* è¿žæŽ¥æµ‹è¯•æŒ‰é’® */}
            <button
              onClick={testConnection}
              style={{
                ...styles.button,
                backgroundColor: '#059669',
                width: '100%',
                marginBottom: '1rem',
                justifyContent: 'center'
              }}
              disabled={isLoading}
            >
              <RefreshCw size={16} />
              {isLoading ? 'æµ‹è¯•ä¸­...' : 'æµ‹è¯•åŽç«¯è¿žæŽ¥'}
            </button>

            <div style={{
              marginBottom: '1.5rem',
              padding: '1rem',
              backgroundColor: '#eff6ff',
              borderRadius: '0.5rem',
              border: '1px solid #dbeafe'
            }}>
              <div style={{...styles.flexBetween, marginBottom: '0.5rem'}}>
                <div style={styles.flexCenter}>
                  <Monitor size={16} color="#2563eb" style={{marginRight: '0.5rem'}} />
                  <span style={{fontSize: '0.875rem', fontWeight: '500', color: '#1e3a8a'}}>
                    æœåŠ¡å™¨IPæ£€æµ‹
                  </span>
                </div>
                <button
                  onClick={fetchUserIP}
                  style={{
                    ...styles.buttonSmall,
                    backgroundColor: isLoading ? '#6b7280' : '#2563eb'
                  }}
                  disabled={isLoading}
                >
                  <RefreshCw size={12} />
                  {isLoading ? 'æ£€æµ‹ä¸­' : 'é‡æ–°æ£€æµ‹'}
                </button>
              </div>

              <div style={{marginBottom: '0.5rem'}}>
                {isLoading ? (
                  <div style={{display: 'flex', alignItems: 'center', gap: '0.5rem'}}>
                    <div style={{
                      width: '16px',
                      height: '16px',
                      border: '2px solid #dbeafe',
                      borderTop: '2px solid #2563eb',
                      borderRadius: '50%',
                      animation: 'spin 1s linear infinite'
                    }}></div>
                    <span style={{fontSize: '0.875rem', color: '#6b7280'}}>
                      æ­£åœ¨ä»ŽæœåŠ¡å™¨èŽ·å–IPåœ°å€...
                    </span>
                  </div>
                ) : userIP ? (
                  <div style={{display: 'flex', alignItems: 'center', gap: '0.5rem'}}>
                    <CheckCircle2 size={16} color="#10b981" />
                    <span style={{fontFamily: 'monospace', fontSize: '1rem', color: '#1e40af', fontWeight: 'bold'}}>
                      {userIP}
                    </span>
                  </div>
                ) : (
                  <div style={{display: 'flex', alignItems: 'center', gap: '0.5rem'}}>
                    <XCircle size={16} color="#dc2626" />
                    <span style={{fontSize: '0.875rem', color: '#dc2626'}}>
                      IPæ£€æµ‹å¤±è´¥
                    </span>
                  </div>
                )}
              </div>

