import React, { useState, useEffect, useCallback } from 'react';
import { Calendar, Clock, Users, CheckCircle2, XCircle, LogOut, UserCheck, Shield, Lock, RefreshCw, Monitor } from 'lucide-react';

const AttendanceSystem = () => {
  // Á≥ªÁªüÁä∂ÊÄÅ
  const [currentTime, setCurrentTime] = useState(new Date());
  const [currentUser, setCurrentUser] = useState(null);
  const [currentView, setCurrentView] = useState('login');

  // IPÁõ∏ÂÖ≥Áä∂ÊÄÅ
  const [userIP, setUserIP] = useState('');
  const [ipStatus, setIpStatus] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  // Ê∂àÊÅØÊèêÁ§∫
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('info');

  // ÁôªÂΩïË°®Âçï
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });

  // ÁÆ°ÁêÜÂëòÁªüËÆ°Êï∞ÊçÆ
  const [adminStats, setAdminStats] = useState({});
  
  // ÁÆ°ÁêÜÂëòÊï∞ÊçÆ
  const [employees, setEmployees] = useState([]);
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [users, setUsers] = useState([]);
  const [adminView, setAdminView] = useState('dashboard'); // dashboard, records, employees, departments, users

  // Áî®Êà∑ÁÆ°ÁêÜÁõ∏ÂÖ≥Áä∂ÊÄÅ
  const [showUserModal, setShowUserModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [userForm, setUserForm] = useState({
    username: '',
    password: '',
    role: 'employee',
    name: '',
    department: '',
    allowed_ips: []
  });

  // Á≠æÂà∞Áä∂ÊÄÅ
  const [checkinStatus, setCheckinStatus] = useState({ checked_in: false, checkin_time: null });

  // API Âü∫Á°Ä URL
  const API_BASE = process.env.REACT_APP_API_URL || 'https://attendance-api-q9qm.onrender.com/api';

  // Ëé∑ÂèñÂ≠òÂÇ®ÁöÑ token
  const getToken = () => localStorage.getItem('attendance_token');

  // ËÆæÁΩÆ token
  const setToken = (token) => {
    if (token) {
      localStorage.setItem('attendance_token', token);
    } else {
      localStorage.removeItem('attendance_token');
    }
  };

  // API ËØ∑Ê±ÇÂ∞ÅË£Ö
  const apiRequest = async (endpoint, options = {}) => {
    const token = getToken();
    const config = {
      headers: {
        'Content-Type': 'application/json',
        ...(token && { Authorization: `Bearer ${token}` }),
        ...options.headers,
      },
      ...options,
    };

    try {
      const response = await fetch(`${API_BASE}${endpoint}`, config);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || `HTTP ${response.status}`);
      }

      return data;
    } catch (error) {
      console.error('APIËØ∑Ê±ÇÈîôËØØ:', error);
      throw error;
    }
  };

  // Ëé∑ÂèñÊúçÂä°Âô®Ê£ÄÊµãÁöÑIPÂú∞ÂùÄ
  const fetchUserIP = useCallback(async () => {
    setIsLoading(true);
    setIpStatus('Ê≠£Âú®‰ªéÊúçÂä°Âô®Ëé∑ÂèñÊú¨Êú∫IPÂú∞ÂùÄ...');

    try {
