  const exportRecords = () => {
    if (attendanceRecords.length === 0) {
      showMessage('ÊöÇÊó†Êï∞ÊçÆÂèØÂØºÂá∫', 'warning');
      return;
    }

    // Ëé∑ÂèñÂΩìÂâçÊúà‰ªΩ
    const now = new Date();
    const currentMonth = now.getMonth() + 1;
    const currentYear = now.getFullYear();

    // Ëé∑ÂèñÊú¨ÊúàÊâÄÊúâÂëòÂ∑•
    const employeeNames = [...new Set(attendanceRecords.map(record => record.employee_name))];

    // Ëé∑ÂèñÊú¨ÊúàÂ§©Êï∞
    const daysInMonth = new Date(currentYear, currentMonth, 0).getDate();

    // ÊûÑÂª∫ËÆæËÆ°Èô¢ËÄÉÂã§Ë°®Ê†ºÂºè
    const csvData = [];

    // Ê†áÈ¢òË°å
    csvData.push(['‰πåÂÖ∞ÂØüÂ∏ÉÈ°πÁõÆÂÖ¨Âè∏ËÄÉÂã§Ë°®']);

    // Ë°®Â§¥ÔºöËÄÉÂã§ÊúàÂ∫¶„ÄÅËÄÉÂã§Êó•Êúü„ÄÅÂêÑÂëòÂ∑•ÂßìÂêçÔºàÊØè‰∏™ÂëòÂ∑•‰∏§ÂàóÔºö‰∏äÂçà„ÄÅ‰∏ãÂçàÔºâ
    const headerRow1 = ['ËÄÉÂã§ÊúàÂ∫¶', 'ËÄÉÂã§Êó•Êúü'];
    const headerRow2 = ['', ''];

    employeeNames.forEach(name => {
      headerRow1.push(name, ''); // ÂëòÂ∑•ÂßìÂêçÂç†‰∏§Âàó
      headerRow2.push('‰∏äÂçà', '‰∏ãÂçà'); // Á¨¨‰∫åË°åÊòæÁ§∫‰∏äÂçà/‰∏ãÂçà
    });

    csvData.push(headerRow1);
    csvData.push(headerRow2);

    // ÊåâÊó•ÊúüÁîüÊàêÊØè‰∏ÄË°å
    for (let day = 1; day <= daysInMonth; day++) {
      const dateStr = `${currentYear}-${String(currentMonth).padStart(2, '0')}-${String(day).padStart(2, '0')}`;
      const monthDayStr = `${currentMonth}Êúà`;
      const dayStr = `${day}Êó•`;

      // ÊØè‰∏ÄË°åÔºöÊúà‰ªΩ„ÄÅÊó•Êúü„ÄÅÂêÑÂëòÂ∑•ÂΩìÂ§©ÁöÑÁ≠æÂà∞Êó∂Èó¥
      const row = [monthDayStr, dayStr];

      // ‰∏∫ÊØè‰∏™ÂëòÂ∑•Ê∑ªÂä†ÂΩìÂ§©ÁöÑÁ≠æÂà∞Êó∂Èó¥Ôºà‰∏äÂçà„ÄÅ‰∏ãÂçàÔºâ
      employeeNames.forEach(employeeName => {
        const dayRecord = attendanceRecords.find(record =>
          record.employee_name === employeeName && record.check_date === dateStr
        );

        if (dayRecord) {
          const checkTime = dayRecord.check_time;
          const hour = parseInt(checkTime.split(':')[0]);

          // Ê†πÊçÆÁ≠æÂà∞Êó∂Èó¥Âà§Êñ≠ÊòØ‰∏äÂçàËøòÊòØ‰∏ãÂçà
          if (hour < 12) {
            // ‰∏äÂçàÁ≠æÂà∞
            row.push(checkTime, ''); // ‰∏äÂçàÊúâÊó∂Èó¥Ôºå‰∏ãÂçàÁ©∫ÁôΩ
          } else {
            // ‰∏ãÂçàÁ≠æÂà∞
            row.push('', checkTime); // ‰∏äÂçàÁ©∫ÁôΩÔºå‰∏ãÂçàÊúâÊó∂Èó¥
          }
        } else {
          // Ê≤°ÊúâÁ≠æÂà∞ËÆ∞ÂΩï
          row.push('', ''); // ‰∏äÂçà‰∏ãÂçàÈÉΩÁ©∫ÁôΩ
        }
      });

      csvData.push(row);
    }

    // ÁªüËÆ°Ë°å
    const attendanceRow = ['Âá∫Âã§Â§©Êï∞', ''];
    const leaveRow = ['ËØ∑ÂÅáÂ§©Êï∞', ''];
    const absentRow = ['Êó∑Â∑•Â§©Êï∞', ''];

    employeeNames.forEach(name => {
      const attendanceDays = attendanceRecords.filter(record => record.employee_name === name).length;
      attendanceRow.push(attendanceDays, ''); // Âá∫Âã§Â§©Êï∞Âè™ÊòæÁ§∫Âú®Á¨¨‰∏ÄÂàó
      leaveRow.push('0', ''); // ËØ∑ÂÅáÂ§©Êï∞
      absentRow.push(daysInMonth - attendanceDays, ''); // Êó∑Â∑•Â§©Êï∞
    });

    csvData.push(attendanceRow);
    csvData.push(leaveRow);
    csvData.push(absentRow);

    // Á≠æÂêçË°å
    const signRow = ['È°πÁõÆË¥üË¥£‰∫∫Á°ÆËÆ§Ôºö', ''];
    employeeNames.forEach(() => {
      signRow.push('', '');
    });
    csvData.push(signRow);

    // ËΩ¨Êç¢‰∏∫CSVÊ†ºÂºè
    const csvContent = csvData.map(row =>
      row.map(field => `"${field}"`).join(',')
    ).join('\n');

    // Ê∑ªÂä†BOM‰ª•ÊîØÊåÅ‰∏≠Êñá
    const BOM = '\uFEFF';
    const csvWithBOM = BOM + csvContent;

    // ÂàõÂª∫‰∏ãËΩΩ
    const blob = new Blob([csvWithBOM], { type: 'text/csv;charset=utf-8' });
    const url = URL.createObjectURL(blob);
