                    <form onSubmit={handleUserSubmit}>
                      <div style={{marginBottom: '1rem'}}>
                        <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem'}}>
                          Áî®Êà∑Âêç *
                        </label>
                        <input
                          type="text"
                          value={userForm.username}
                          onChange={(e) => setUserForm(prev => ({ ...prev, username: e.target.value }))}
                          style={styles.input}
                          placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑Âêç"
                          required
                        />
                      </div>

                      <div style={{marginBottom: '1rem'}}>
                        <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem'}}>
                          ÂØÜÁ†Å {editingUser && '(ÁïôÁ©∫Âàô‰∏ç‰øÆÊîπ)'}
                        </label>
                        <input
                          type="password"
                          value={userForm.password}
                          onChange={(e) => setUserForm(prev => ({ ...prev, password: e.target.value }))}
                          style={styles.input}
                          placeholder={editingUser ? "ÁïôÁ©∫Âàô‰∏ç‰øÆÊîπÂØÜÁ†Å" : "ËØ∑ËæìÂÖ•ÂØÜÁ†Å"}
                          required={!editingUser}
                        />
                      </div>

                      <div style={{marginBottom: '1rem'}}>
                        <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem'}}>
                          ÂßìÂêç *
                        </label>
                        <input
                          type="text"
                          value={userForm.name}
                          onChange={(e) => setUserForm(prev => ({ ...prev, name: e.target.value }))}
                          style={styles.input}
                          placeholder="ËØ∑ËæìÂÖ•ÁúüÂÆûÂßìÂêç"
                          required
                        />
                      </div>

                      <div style={{marginBottom: '1rem'}}>
                        <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem'}}>
                          ÈÉ®Èó® *
                        </label>
                        <input
                          type="text"
                          value={userForm.department}
                          onChange={(e) => setUserForm(prev => ({ ...prev, department: e.target.value }))}
                          style={styles.input}
                          placeholder="ËØ∑ËæìÂÖ•ÈÉ®Èó®ÂêçÁß∞"
                          required
                        />
                      </div>

                      <div style={{marginBottom: '1rem'}}>
                        <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem'}}>
                          ËßíËâ≤
                        </label>
                        <select
                          value={userForm.role}
                          onChange={(e) => setUserForm(prev => ({ ...prev, role: e.target.value }))}
                          style={styles.input}
                        >
                          <option value="employee">ÂëòÂ∑•</option>
                          <option value="admin">ÁÆ°ÁêÜÂëò</option>
                        </select>
                      </div>

                      <div style={{marginBottom: '1rem'}}>
                        <label style={{display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem'}}>
                          ÊéàÊùÉIPÂú∞ÂùÄ (Â§ö‰∏™IPÁî®ÈÄóÂè∑ÂàÜÈöî)
                        </label>
                        <input
                          type="text"
                          value={userForm.allowed_ips.join(', ')}
                          onChange={(e) => setUserForm(prev => ({
                            ...prev,
                            allowed_ips: e.target.value.split(',').map(ip => ip.trim()).filter(ip => ip)
                          }))}
                          style={styles.input}
                          placeholder="192.168.1.100, 192.168.1.101"
                        />
                      </div>

                      <div style={{display: 'flex', gap: '0.5rem', justifyContent: 'flex-end'}}>
                        <button
                          type="button"
                          onClick={() => {
                            setShowUserModal(false);
                            setEditingUser(null);
                          }}
                          style={{
                            ...styles.button,
                            backgroundColor: '#6b7280'
                          }}
                        >
                          ÂèñÊ∂à
                        </button>
                        <button
                          type="submit"
                          style={{
                            ...styles.button,
                            backgroundColor: '#059669'
                          }}
                          disabled={isLoading}
                        >
                          {isLoading ? '‰øùÂ≠ò‰∏≠...' : (editingUser ? 'Êõ¥Êñ∞' : 'ÂàõÂª∫')}
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              )}
            </div>
          )}

          {/* Ê∂àÊÅØÊèêÁ§∫ */}
          {message && (
            <div style={{
              position: 'fixed',
              top: '1rem',
              right: '1rem',
              padding: '1rem',
              borderRadius: '0.375rem',
              zIndex: 1000,
              ...(messageType === 'success' ? styles.messageSuccess :
                 messageType === 'error' ? styles.messageError : styles.messageWarning)
            }}>
              {message}
            </div>
          )}
        </div>
      </div>
    );
  }

  return null;
};

export default AttendanceSystem;
