import React, { useState, useEffect, useCallback } from 'react';
import { Calendar, Clock, Users, CheckCircle2, XCircle, LogOut, UserCheck, Shield, Lock, RefreshCw, Monitor } from 'lucide-react';

const AttendanceSystem = () => {
  // 系统状态
  const [currentTime, setCurrentTime] = useState(new Date());
  const [currentUser, setCurrentUser] = useState(null);
  const [currentView, setCurrentView] = useState('login');

  // IP相关状态
  const [userIP, setUserIP] = useState('');
  const [ipStatus, setIpStatus] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  // 消息提示
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('info');

  // 登录表单
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });

  // 管理员统计数据
  const [adminStats, setAdminStats] = useState({});

  // 管理员数据
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [users, setUsers] = useState([]);
  const [adminView, setAdminView] = useState('dashboard');

  // 用户管理相关状态
  const [showUserModal, setShowUserModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [userForm, setUserForm] = useState({
    username: '',
    password: '',
    role: 'employee',
    name: '',
    department: '',
    allowed_ips: []
  });

  // 签到状态
  const [checkinStatus, setCheckinStatus] = useState({ checked_in: false, checkin_time: null });

  // API 基础 URL
  const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  // 获取存储的 token
  const getToken = () => localStorage.getItem('attendance_token');

  // 设置 token
  const setToken = (token) => {
    if (token) {
      localStorage.setItem('attendance_token', token);
    } else {
      localStorage.removeItem('attendance_token');
    }
  };

  // API 请求封装
  const apiRequest = async (endpoint, options = {}) => {
    const token = getToken();
    const config = {
      headers: {
        'Content-Type': 'application/json',
        ...(token && { Authorization: `Bearer ${token}` }),
        ...options.headers,
      },
      ...options,
    };

    try {
      console.log(`API请求: ${API_BASE}${endpoint}`, config);
      const response = await fetch(`${API_BASE}${endpoint}`, config);

      // 检查响应状态
      if (response.status === 401 || response.status === 403) {
        console.warn('认证失败，可能是Token过期');
        setToken(null);
        localStorage.removeItem('attendance_user');
        setCurrentUser(null);
        setCurrentView('login');
        showMessage('登录已过期，请重新登录', 'warning');
        throw new Error('认证失败，请重新登录');
      }

      const data = await response.json();
      console.log(`API响应: ${endpoint}`, data);

      if (!response.ok) {
        throw new Error(data.error || `HTTP ${response.status}: ${response.statusText}`);
      }

      return data;
    } catch (error) {
      console.error(`API请求错误 ${endpoint}:`, error);

      if (error.name === 'TypeError' && error.message.includes('fetch')) {
        throw new Error('无法连接到服务器，请检查后端是否正在运行');
      }

      throw error;
    }
  };

  // 获取服务器检测的IP地址
  const fetchUserIP = useCallback(async () => {
    setIsLoading(true);
    setIpStatus('正在从服务器获取本机IP地址...');

    try {
      const data = await apiRequest('/ip');
      setUserIP(data.ip);
      setIpStatus(`服务器检测到IP: ${data.ip}`);
      console.log('[IP检测] 服务器获取成功:', data.ip);
    } catch (error) {
      console.error('[IP检测] 服务器获取失败:', error);
      setUserIP('');
      setIpStatus(`获取失败：${error.message}`);
    } finally {
      setIsLoading(false);
    }
  }, []);

  // 验证 token 并自动登录
  const validateToken = useCallback(async () => {
    const token = getToken();
    if (!token) return;

    try {
      await apiRequest('/checkin/status');
      const userData = JSON.parse(localStorage.getItem('attendance_user'));
      if (userData) {
        setCurrentUser(userData);
        setCurrentView(userData.role === 'admin' ? 'admin' : 'checkin');
      }
    } catch (error) {
      console.log('Token 无效，需要重新登录');
      setToken(null);
      localStorage.removeItem('attendance_user');
    }
  }, []);

  // 获取签到状态
  const fetchCheckinStatus = useCallback(async () => {
    if (!currentUser || currentUser.role === 'admin') return;

    try {
      const data = await apiRequest('/checkin/status');
      setCheckinStatus(data);
    } catch (error) {
      console.error('获取签到状态失败:', error);
    }
  }, [currentUser]);

  // 获取管理员统计数据
  const fetchAdminStats = useCallback(async () => {
    if (!currentUser || currentUser.role !== 'admin') return;

    try {
      const data = await apiRequest('/admin/stats');
      setAdminStats(data);
    } catch (error) {
      console.error('获取统计数据失败:', error);
      setAdminStats({
        total_employees: users.filter(u => u.role === 'employee').length,
        checked_in_today: attendanceRecords.filter(r => r.check_date === new Date().toISOString().split('T')[0]).length,
        attendance_rate: 0
      });
    }
  }, [currentUser, users, attendanceRecords]);

  // 获取考勤记录
  const fetchAttendanceRecords = useCallback(async () => {
    if (!currentUser || currentUser.role !== 'admin') return;

    try {
      const data = await apiRequest('/admin/records');
      setAttendanceRecords(data.records || []);
    } catch (error) {
      console.error('获取考勤记录失败:', error);
      setAttendanceRecords([]);
    }
  }, [currentUser]);

  // 获取用户列表
  const fetchUsers = useCallback(async () => {
    if (!currentUser || currentUser.role !== 'admin') return;

    try {
      setIsLoading(true);
      // 优先使用管理员API
      const data = await apiRequest('/admin/users');
      setUsers(data.users || []);

    } catch (error) {
      console.error('获取用户列表失败，尝试备用接口:', error);
      // 备用：使用debug接口
      try {
        const data = await apiRequest('/debug/users');
        const usersData = data.users || [];
        const formattedUsers = usersData.map(user => ({
          ...user,
          allowed_ips: user.allowed_ips ? JSON.parse(user.allowed_ips) : []
        }));
        setUsers(formattedUsers);
      } catch (backupError) {
        console.error('备用接口也失败:', backupError);
        setUsers([]);
        showMessage('无法加载用户数据：' + backupError.message, 'error');
      }
    } finally {
      setIsLoading(false);
    }
  }, [currentUser]);

  // 删除用户
  const deleteUser = async (userId, userName) => {
    if (!window.confirm(`确定要删除用户"${userName}"吗？此操作不可撤销！`)) {
      return;
    }

    try {
      setIsLoading(true);
      await apiRequest(`/admin/users/${userId}`, {
        method: 'DELETE'
      });
      showMessage('用户删除成功', 'success');
      fetchUsers(); // 刷新列表
    } catch (error) {
      showMessage(error.message, 'error');
    } finally {
      setIsLoading(false);
    }
  };

  // 编辑用户
  const editUser = (user) => {
    setEditingUser(user);
    setUserForm({
      username: user.username,
      password: '', // 密码字段留空
      role: user.role,
      name: user.name,
      department: user.department,
      allowed_ips: Array.isArray(user.allowed_ips) ? user.allowed_ips : []
    });
    setShowUserModal(true);
  };

  // 新增用户
  const addNewUser = () => {
    setEditingUser(null);
    setUserForm({
      username: '',
      password: '',
      role: 'employee',
      name: '',
      department: '',
      allowed_ips: [userIP] // 默认添加当前IP
    });
    setShowUserModal(true);
  };

  // 创建或更新用户
  const handleUserSubmit = async (e) => {
    e.preventDefault();

    try {
      setIsLoading(true);

      if (editingUser) {
        // 更新用户
        await apiRequest(`/admin/users/${editingUser.id}`, {
          method: 'PUT',
          body: JSON.stringify(userForm)
        });
        showMessage('用户更新成功', 'success');
      } else {
        // 创建新用户
        await apiRequest('/admin/users', {
          method: 'POST',
          body: JSON.stringify(userForm)
        });
        showMessage('用户创建成功', 'success');
      }

      setShowUserModal(false);
      setEditingUser(null);
      setUserForm({
        username: '',
        password: '',
        role: 'employee',
        name: '',
        department: '',
        allowed_ips: []
      });

      fetchUsers(); // 刷新用户列表
    } catch (error) {
      showMessage(error.message, 'error');
    } finally {
      setIsLoading(false);
    }
  };

  // 导出考勤记录（设计院格式）
  const exportAttendanceRecords = async () => {
    try {
      setIsLoading(true);
      const currentDate = new Date();
      const year = currentDate.getFullYear();
      const month = currentDate.getMonth() + 1;

      const data = await apiRequest(`/admin/export?year=${year}&month=${month}`);

      // 构建设计院考勤表格式的CSV数据
      generateDesignInstituteCSV(data.data);
      showMessage('考勤表导出成功', 'success');

    } catch (error) {
      showMessage('导出失败：' + error.message, 'error');
    } finally {
      setIsLoading(false);
    }
  };

  // 生成设计院格式的CSV文件
  const generateDesignInstituteCSV = (exportData) => {
    const { title, year, month, employees, days, records } = exportData;

    // 构建CSV数据
    const csvData = [];

    // 标题行
    csvData.push([title]);
    csvData.push([]); // 空行

    // 表头行1：考勤月度、考勤日期、各员工姓名
    const headerRow1 = ['考勤月度', '考勤日期'];
    employees.forEach(name => {
      headerRow1.push(name);
    });
    csvData.push(headerRow1);

    // 按日期生成每一行
    for (let day = 1; day <= days; day++) {
      const dateStr = `${year}-${String(month).padStart(2, '0')}-${String(day).padStart(2, '0')}`;
      const monthStr = `${month}月`;
      const dayStr = `${day}日`;

      const row = [monthStr, dayStr];

      // 为每个员工添加当天的签到时间
      employees.forEach(employeeName => {
        const dayRecord = records.find(record =>
          record.employee_name === employeeName && record.check_date === dateStr
        );

        if (dayRecord) {
          row.push(dayRecord.check_time);
        } else {
          row.push(''); // 未签到
        }
      });

      csvData.push(row);
    }

    // 添加统计行
    csvData.push([]); // 空行

    const attendanceRow = ['出勤天数', ''];
    const absentRow = ['缺勤天数', ''];

    employees.forEach(name => {
      const attendanceDays = records.filter(record => record.employee_name === name).length;
      attendanceRow.push(attendanceDays);
      absentRow.push(days - attendanceDays);
    });

    csvData.push(attendanceRow);
    csvData.push(absentRow);

    // 签字行
    csvData.push([]);
    csvData.push(['项目负责人签字：', '', '日期：']);

    // 转换为CSV格式并下载
    const csvContent = csvData.map(row =>
      row.map(field => `"${field}"`).join(',')
    ).join('\n');

    // 添加BOM以支持中文
    const BOM = '\uFEFF';
    const csvWithBOM = BOM + csvContent;

    // 创建下载
    const blob = new Blob([csvWithBOM], { type: 'text/csv;charset=utf-8' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `乌兰察布项目考勤表_${year}年${month}月.csv`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  useEffect(() => {
    fetchUserIP();
    validateToken();

    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => clearInterval(timer);
